import React, { useState, useEffect } from "react";
import Crop75SharpIcon from "@mui/icons-material/Crop75Sharp";
import "./Styles.css";
import { useDrawingTools } from "../Context/DrawingToolsContext";

const Rectangle = ({ canvasRef, saveCanvasState }) => {
  const { setSelectedTool } = useDrawingTools();
  const [isDrawing, setIsDrawing] = useState(false);
  const [startX, setStartX] = useState(0);
  const [startY, setStartY] = useState(0);

  const handleMouseDown = (e) => {
    setIsDrawing(true);
    setStartX(e.x);
    setStartY(e.y);
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) return;
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    const endX = e.nativeEvent.offsetX;
    const endY = e.nativeEvent.offsetY;
    console.log(endX, endY);
    const width = endX - startX;
    const height = endY - startY;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillRect(startX, startY, width, height);
  };

  const handleMouseUp = () => {
    if (!isDrawing) return;
    setIsDrawing(false);
    saveCanvasState();
  };

  const handleClick = () => {
    if (!canvasRef.current) return;
    setSelectedTool("Rectangle");
    const canvas = canvasRef.current;
    console.log("Rectangle selected")
    canvas.addEventListener("mousedown", handleMouseDown);
    canvas.addEventListener("mousemove", handleMouseMove);
    canvas.addEventListener("mouseup", handleMouseUp);
  };

  // useEffect(() => {
  //   return () => {
  //     const canvas = canvasRef.current;
  //     if (!canvas) return;
  //     canvas.removeEventListener("mousedown", handleMouseDown);
  //     canvas.removeEventListener("mousemove", handleMouseMove);
  //     canvas.removeEventListener("mouseup", handleMouseUp);
  //   };
  // }, [canvasRef]);

  return (
    <div className="shapes">
      <Crop75SharpIcon onClick={handleClick} />
    </div>
  );
};

export default Rectangle;
