import React from 'react';
import { Slider, Typography } from '@mui/material';
import { useDrawingTools } from '../Context/DrawingToolsContext';
import { Icon } from "@iconify/react";

const Eraser = () => {
  const { eraserWidth,setEraserWidth, setSelectedTool} = useDrawingTools();
  const min = 1;
  const max = 10;

  const handleLineWidthChange = (e, newValue) => {
    setEraserWidth(newValue);
  };
  const handleEraser = () => {
    setSelectedTool("Eraser");
  }

  return (
    <div>
      <Icon icon="mdi-eraser" width="30" height="30" onClick={handleEraser} />
      {/* <Slider value={eraserWidth} onChange={handleLineWidthChange} min={min} max={max} />
      <Typography variant="body2" sx={{ textAlign: 'center', padding: 2 }}>
        Thickness: {eraserWidth}
      </Typography> */}
    </div>
  );
};

export default Eraser;
