import React, { useState, useEffect, useRef } from 'react';
import './Canvas.css';

function DrawingCanvas() {
  // Create a reference to the canvas element
  const canvasRef = useRef(null);

  // Define two pieces of state:
  // - 'drawing' to track whether the user is currently drawing (initially set to 'false')
  // - 'context' to hold the 2D drawing context of the canvas
  const [drawing, setDrawing] = useState(false);
  const [context, setContext] = useState(null);

  // useEffect runs when the component is mounted
  useEffect(() => {
    // Get a reference to the canvas element using the 'canvasRef'
    const canvas = canvasRef.current;

    // Get the 2D drawing context of the canvas
    const ctx = canvas.getContext('2d');

    // Set the 'context' state to the 2D drawing context
    setContext(ctx);
  }, []);

  // Event handler for when the user presses the mouse button (mousedown)
  const handleMouseDown = (e) => {
    // Set 'drawing' to 'true' to indicate that the user is currently drawing
    setDrawing(true);

    // Begin a new drawing path at the current mouse position
    context.beginPath();
    context.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
  };

  // Event handler for when the user moves the mouse (mousemove)
  const handleMouseMove = (e) => {
    // If the user is not drawing, exit the function
    if (!drawing) return;

    // Continue drawing a line from the previous position to the current mouse position
    context.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    context.stroke(); // Stroke the path to create the line
  };

  // Event handler for when the user releases the mouse button (mouseup)
  const handleMouseUp = () => {
    // Set 'drawing' to 'false' to indicate that the user has stopped drawing
    setDrawing(false);

    // Close the current drawing path
    context.closePath();
  };

  return (
    <canvas
      ref={canvasRef}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    />
  );
}

export default DrawingCanvas;
