import React, { useState } from 'react';
import { Button, Popover, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import FiberManualRecordIcon from '@mui/icons-material/FiberManualRecord';


const ShapesMenu = () => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleShapeClick = (shape) => {
    // Handle the selected shape (e.g., start drawing it)
    console.log('Selected shape:', shape);
    handleClose();
  };

  return (
    <div>
      <Button
        variant="contained"
        color="primary"
        onClick={handleClick}
        startIcon={<AddCircleIcon />}
      >
        Select Shape
      </Button>
      <Popover
        open={Boolean(anchorEl)}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'center',
        }}
      >
        <List>
          <ListItem button onClick={() => handleShapeClick('Circle')}>
            <ListItemIcon>
              <FiberManualRecordIcon color="primary" />
            </ListItemIcon>
            <ListItemText primary="Circle" />
          </ListItem>
          <ListItem button onClick={() => handleShapeClick('Rectangle')}>
            <ListItemIcon>
              <FiberManualRecordIcon color="primary" />
            </ListItemIcon>
            <ListItemText primary="Rectangle" />
          </ListItem>
          <ListItem button onClick={() => handleShapeClick('Line')}>
            <ListItemIcon>
              <FiberManualRecordIcon color="primary" />
            </ListItemIcon>
            <ListItemText primary="Line" />
          </ListItem>
        </List>
      </Popover>
    </div>
  );
};

export default ShapesMenu;
