import React from "react";
import UndoRoundedIcon from "@mui/icons-material/UndoRounded";
import RedoRoundedIcon from "@mui/icons-material/RedoRounded";
import { useHistory } from "../Context/History";
import { Box } from "@mui/material";

const UndoRedo = ({ redrawCanvas }) => {
  const { undo, redo } = useHistory();
  const handleUndo = () => {
    const data = undo();
    if (data) {
      redrawCanvas(data);
    }
  };
  const handleRedo = () => {
    const data = redo();
    if (data) {
      redrawCanvas(data);
    }
  };

  return (
    <Box sx={{display:"flex",flexDirection:"column",justifyContent:"center", position:"absolute", top:"10vh"}}>
      <UndoRoundedIcon onClick={handleUndo} style={{width:"50", height:"50"}}/>
      <RedoRoundedIcon onClick={handleRedo} style={{width:"50", height:"50"}}/>
    </Box>
  );
};

export default UndoRedo;
