import React, { useState, useEffect, useRef } from "react";
import Pencil from "./Drawing_Tools/pencil";
import { useDrawingTools } from "./Context/DrawingToolsContext";
import "./Cursors.css";

function DrawingCanvas() {
  const canvasRef = useRef(null);
  const { selectedTool, color, lineWidth } = useDrawingTools();
  const [drawing, setDrawing] = useState(false);
  const [context, setContext] = useState(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    if (selectedTool === "pencil") {
      canvas.classList.add("pencil-cursor");
    }
    ctx.strokeStyle = color;
    ctx.lineWidth = lineWidth;
    setContext(ctx);
  }, [color, lineWidth, selectedTool]);
  const handlePointerDown = (e) => {
    if (isStylus(e)) {
      setDrawing(true);
      context.beginPath();
      context.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    }
  };

  const handlePointerMove = (e) => {
    if (drawing && isStylus(e)) {
      context.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
      context.stroke();
    }
  };

  const handlePointerUp = (e) => {
    if (drawing && isStylus(e)) {
      setDrawing(false);
      context.closePath();
    }
  };

  // Helper function to check if the event is from a stylus or pen
  const isStylus = (e) => {
    return (
      e.pointerType === "pen" ||
      e.pointerType === "stylus" ||
      (e.pointerType === "mouse" && e.buttons === 32) // Check for stylus-like mouse event
    );
  };

  return (
    <>
      <Pencil />
      <canvas
        ref={canvasRef}
        onPointerDown={handlePointerDown}
        onPointerMove={handlePointerMove}
        onPointerUp={handlePointerUp}
        width={1200}
        height={800}
      />
    </>
  );
}

export default DrawingCanvas;
