import React from 'react';
import { Slider, Typography } from '@mui/material';
import { useDrawingTools } from '../Context/DrawingToolsContext';

const EraserTool = () => {
  const { lineWidth, setLineWidth } = useDrawingTools();
  const min = 1;
  const max = 10;

  const handleLineWidthChange = (e, newValue) => {
    setLineWidth(newValue);
  };

  return (
    <div>
      <Slider value={lineWidth} onChange={handleLineWidthChange} min={min} max={max} />
      <Typography variant="body2" sx={{ textAlign: 'center', padding: 2 }}>
        Thickness: {lineWidth}
      </Typography>
    </div>
  );
};

export default EraserTool;
