import React, { useState } from 'react';
import {
  Box,
  Popover,
  Typography,
  Slider,
  IconButton,
} from '@mui/material';
import BrushIcon from '@mui/icons-material/Brush';

function ThicknessSlider() {
  const [anchorEl, setAnchorEl] = useState(null);
  const [thickness, setThickness] = useState(5);

  const handleOpenPopover = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClosePopover = () => {
    setAnchorEl(null);
  };

  const handleThicknessChange = (event, newValue) => {
    setThickness(newValue);
  };

  return (
    <Box>
      <IconButton onClick={handleOpenPopover}>
        <BrushIcon />
      </IconButton>
      <Popover
        open={Boolean(anchorEl)}
        anchorEl={anchorEl}
        onClose={handleClosePopover}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'center',
        }}
      >
        <Box p={2}>
          <Typography variant="body2">Adjust Thickness</Typography>
          <Slider
            value={thickness}
            onChange={handleThicknessChange}
            min={1}
            max={20}
            valueLabelDisplay="auto"
          />
        </Box>
      </Popover>
      <Typography variant="body2">Thickness: {thickness}</Typography>
    </Box>
  );
}

export default ThicknessSlider;
