import React, { useState } from "react";

const UploadFiles = () => {
  const [file, setFile] = useState(null);

  return (
    <div>
      <input
        type="file"
        onChange={(e) => {
          setFile(e.target.files[0]);
        }}
        placeholder="Upload an file"
        style={{ backgroundColor: "red" }}
      />
    </div>
  );
};

export default UploadFiles;
