import React, { useState } from "react";
import { Popover, Grid } from "@mui/material";
import InterestsIcon from "@mui/icons-material/Interests";
import "./Styles.css";
import Circle from "../Shape_Box/Circle";
import Rectangle from "../Shape_Box/Recatangle";
import Triangle from "../Shape_Box/Triangle";

const ShapesMenu = () => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleShapeClick = (shape) => {
    console.log("Selected shape:", shape);
    handleClose();
  };

  return (
    <div>
      <div className="circular-icon-container" onClick={handleClick}>
        <InterestsIcon
          className="circular-icon"
          sx={{ color: "white", fontSize: "28px" }}
        />
      </div>
      <Popover
        open={Boolean(anchorEl)}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "center",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "center",
        }}
      >
        <Grid container spacing={2}>
          <Grid item xs={4}>
            <Circle onSelect={handleShapeClick} />
          </Grid>
          <Grid item xs={4}>
            <Rectangle onSelect={handleShapeClick} />
          </Grid>
          {/* Add other shape components similarly */}
        </Grid>
      </Popover>
    </div>
  );
};

export default ShapesMenu;
