import React from "react";
import { useDrawingTools } from "../Context/DrawingToolsContext";

function ColorSelector() {
  const { selectedColor, setSelectedColor } = useDrawingTools();

  const handleColorChange = (e) => {
    setSelectedColor(e.target.value);
  };

  return (
    <div>
      <label htmlFor="colorPicker">Select a Color:</label>
      <input
        type="color"
        id="colorPicker"
        value={selectedColor}
        onChange={handleColorChange}
        style={{
          width: "50px",
          height: "50px",
          borderRadius: "50%",
          border: "none",
          boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.5)",
          background:
            "linear-gradient(to bottom, red, orange, yellow, green, blue, indigo, violet)",
        }}
      />
      <p>Selected color: {selectedColor}</p>
      {/* You can use the selectedColor in your application */}
    </div>
  );
}

export default ColorSelector;
