import * as React from "react";
import SpeedDial from "@mui/material/SpeedDial";
import SpeedDialAction from "@mui/material/SpeedDialAction";
import DrawIcon from "@mui/icons-material/Draw";
import BrushIcon from "@mui/icons-material/Brush";
import CreateIcon from "@mui/icons-material/Create";
import Pencil from "../Drawing_Tools/pencil";
import Pen from "../Drawing_Tools/pen";
import Brush from "../Drawing_Tools/brush";
import GestureIcon from "@mui/icons-material/Gesture";
import Eraser from "../Drawing_Tools/Eraser";
import { Icon } from "@iconify/react";
import LineWeightIcon from "@mui/icons-material/LineWeight";
import { Popover, Slider, Typography } from "@mui/material";
import { useState } from "react";
import { useDrawingTools } from "../Context/DrawingToolsContext";

const actions = [
  { icon: <CreateIcon />, name: "Pencil", child: <Pencil /> },
  { icon: <DrawIcon />, name: "Pen", child: <Pen /> },
  { icon: <BrushIcon />, name: "Brush", child: <Brush /> },
  {
    icon: <Icon icon="mdi:eraser" width="30" height="30" />,
    name: "Eraser",
    child: <Eraser />,
  },
  { icon: <LineWeightIcon />, name: "Thickness", child: null },
];

export default function Tools() {
  const { lineWidth, setLineWidth } = useDrawingTools();
  const [open, setOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);

  const handleOpen = (event) => {
    setAnchorEl(event.currentTarget);
    setOpen(true);
  };

  const handleClose = () => {
    setAnchorEl(null);
    setOpen(false);
  };
  return (
    <div>
      <SpeedDial
        ariaLabel="SpeedDial basic example"
        sx={{ position: "absolute", paddingTop: 0.5, paddingLeft: 0.5 }}
        icon={<GestureIcon />}
        direction="down"
      >
        {actions.map((action) => (
          <SpeedDialAction
            key={action.name}
            icon={action.icon}
            tooltipTitle={action.name}
            onClick={action.name === "Thickness" ? handleOpen : null}
          >
            {action.child}
          </SpeedDialAction>
        ))}
      </SpeedDial>
      <Popover
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "center",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "center",
        }}
        p={2}
      >
        <Slider
          value={lineWidth}
          onChange={(e, newValue) => setLineWidth(newValue)}
          min={1}
          max={20}
        />
        <Typography variant="body2" sx={{ textAlign: "center", padding: 2 }}>
          Thickness: {lineWidth}
        </Typography>
      </Popover>
    </div>
  );
}
