import React, { useState } from 'react';
import Icon from '@iconify/react';
import eraserIcon from '@iconify-icons/mdi/eraser';
import { Slider, Typography, Popover } from '@mui/material';

function EraserControl() {
  const [isOpen, setIsOpen] = useState(false);
  const [eraserWidth, setEraserWidth] = useState(10); // Initial eraser width
  const min = 1;
  const max = 50; // Adjust the max value as needed

  const handleIconClick = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleLineWidthChange = (event, newValue) => {
    setEraserWidth(newValue);
  };

  return (
    <div>
      <Icon icon={eraserIcon} width="30" height="30" onClick={handleIconClick} />

      <Popover
        open={isOpen}
        anchorEl={document.body} // You can adjust this anchor as needed
        onClose={handleClose}
        anchorOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'right',
        }}
      >
        <Slider
          value={eraserWidth}
          onChange={handleLineWidthChange}
          min={min}
          max={max}
        />
        <Typography variant="body2" sx={{ textAlign: 'center', padding: 2 }}>
          Thickness: {eraserWidth}
        </Typography>
      </Popover>
    </div>
  );
}

export default EraserControl;
